// Task 1
const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  return (
    <div>
      <p>{props.part1}: {props.excercises1}</p>
      <p></p>
      <p>{props.part2}</p>
      <p>{props.excercises2}</p>
      <p>{props.part3}</p>
      <p>{props.excercises3}</p>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>Number of excercises = {props.total}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const excercises1 = 10
  const part2 = 'Using props to pass data'
  const excercises2 = 7
  const part3 = 'State of a component'
  const excercises3 = 14
  const total = excercises1 + excercises2 + excercises3
  return (
    <div>
      <Header course={course} />
      <Content part1={part1} excercises1={excercises1} />
      <Content part2={part2} excercises2={excercises2} />
      <Content part3={part3} excercises3={excercises3} />
      <Total total={total} />
    </div>
  )
}
export default App;
